import { EAS, PartialTypedDataConfig, Offchain, OffchainAttestationVersion, SchemaEncoder } from "@ethereum-attestation-service/eas-sdk";
import { urlToHttpOptions } from "url";

const singleWorkout = "string title,string sport_type,string type,uint64 moving_time,uint64 distance,string average_speed,uint64 elevation_gain,string timezone,string local_time,uint64 utc_time,string map,bool strava_single_activity,string data_source";

export class Attestation {

    _receipt;
    _schemaEncoder;

    constructor () {

            this._schemaEncoder = new SchemaEncoder(singleWorkout);
    }

    mock () {

        this._receipt = {"signer":"0x77a3b79a2De700AfcfC761fED837a67D7d8fAe1B","sig":{"domain":{"name":"EAS Attestation","version":"1.0.1","chainId":8453,"verifyingContract":"0x4200000000000000000000000000000000000021"},"primaryType":"Attest","message":{"recipient":"0xb6ca51ca72c689b720235aca37e579f821fa05ee","expirationTime":0,"time":1706791801,"revocable":true,"version":1,"nonce":0,"schema":"0x48d9973eb6863978c104f85dc6864e827fc0f72c4083dd853171e0bf034f8774","refUID":"0xaa56fc2757acbcbb010dbd6dd132f66eb9ac37e5cf67896c6057670ede35d958","data":""},"types":{"Attest":[{"name":"version","type":"uint16"},{"name":"schema","type":"bytes32"},{"name":"recipient","type":"address"},{"name":"time","type":"uint64"},{"name":"expirationTime","type":"uint64"},{"name":"revocable","type":"bool"},{"name":"refUID","type":"bytes32"},{"name":"data","type":"bytes"}]},"signature":{"v":28,"r":"0xf3563d063f124a1360523b9a48ffb88e8127cc4e99238c0d6426d70ef8a53437","s":"0x02a913876071e388243ae2d1904997a6f812c133ae6c23407c4d2cc995d838d6"},"uid":"0xcbc28ac601589e07471fa0e09b5af93b804b6db6d77465d5e7ebf4cf64510c38"}};
       //  this._receipt = attestation; // this.chainIdToBigInt(attestation)
    }
    chainIdToBigInt(a: any) {
        a.sig.domain.chainId = BigInt(a.sig.domain.chainId)
        return a;
    }

    _chainIdToBigInt(id: string) {
        return BigInt(id)
    }

    receipt () {

        return this._receipt;
    }

    verify () {

           // is contract on BASE
           const contract =  new EAS("0x4200000000000000000000000000000000000021");

           const EAS_CONFIG: PartialTypedDataConfig = {
               address: this._receipt.sig.domain.verifyingContract,
               version: this._receipt.sig.domain.version,
               chainId: this._chainIdToBigInt(this._receipt.sig.domain.chainId),
           };

           const offchain = new Offchain(EAS_CONFIG, OffchainAttestationVersion.Version1, contract);
           const isValidAttestation = offchain.verifyOffchainAttestationSignature(
            this._receipt.signer,
            this._receipt.sig
           );
           
           return isValidAttestation;
    }

    data() {

        return this._schemaEncoder.decodeData(this._receipt.sig.message.data);
    }
}